    private void btnSimpanActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnSimpanActionPerformed
        String[] data = {eIDT.getText(), eIDP.getText(), eIDPN.getText(), eBiayasewa.getText(), eLamasewa.getText(), eTotalbiaya.getText(), eTanggalsewa.getText(), eTanggalkembali.getText()};
        myObject.simpanPrepareStatement("tbtransaksi", data);   
    
    tampil();
    }//GEN-LAST:event_btnSimpanActionPerformed

    private void btnEditActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnEditActionPerformed
        String[] kolom = {"id_transaksi", "id_pelanggan", "id_penyewaan","biaya_sewa","lama_sewa","total_biaya","tanggal_pinjam","tanggal_kembali"};
        String[] value = {eIDT.getText(), eIDP.getText(), eIDPN.getText(), eBiayasewa.getText(), eLamasewa.getText(), eTotalbiaya.getText(), eTanggalsewa.getText(), eTanggalkembali.getText()};
        new ConfigDB().ubahDataPrepareStatement("tbtransaksi", "id_transaksi", eIDT.getText(), kolom, value);
        tampil();
    }//GEN-LAST:event_btnEditActionPerformed

    private void btnHapusActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnHapusActionPerformed
      String SQL = " DELETE FROM tbtransaksi WHERE " + " id_transaksi= ' " + eIDT.getText() + " '";
      System.out.println(SQL);
      myObject.hapusData(SQL);
      tampil();
    }//GEN-LAST:event_btnHapusActionPerformed

    private void tampilTableMouseClicked(java.awt.event.MouseEvent evt) {//GEN-FIRST:event_tampilTableMouseClicked
        int i = tampilTable.getSelectedRow();
        TableModel model = tampilTable.getModel();
        eIDT.setText(model.getValueAt(i, 0).toString());
        eIDP.setText(model.getValueAt(i, 1).toString());
        eIDPN.setText(model.getValueAt(i, 2).toString());
        eBiayasewa.setText(model.getValueAt(i, 3).toString());
        eLamasewa.setText(model.getValueAt(i, 4).toString());
        eTotalbiaya.setText(model.getValueAt(i, 5).toString());
        eTanggalsewa.setText(model.getValueAt(i, 6).toString());
        eTanggalkembali.setText(model.getValueAt(i, 7).toString());
          
        btnSimpan.setEnabled(true);
        btnEdit.setEnabled(true);
        btnEdit.setEnabled(true);
    }//GEN-LAST:event_tampilTableMouseClicked

