    private void tampil() {
        String judulKolom[]={"ID Transaksi","ID Pelanggan","ID Penyewaan","Biaya Sewa", "Lama Sewa","Total Biaya", "Tanggal Sewa","Tanggal Kembali"};
        int lebarKolom[]={100,100,100,100,100,100,100,100};
        
        new ConfigDB().tampilTable(judulKolom,"SELECT * FROM tbtransaksi", tampilTable);
        new ConfigDB().aturLebarKolom(tampilTable, lebarKolom);
    }
